mapper.select:
select id, user_id, nick_name, order_no, coupon_id, total_amount, coupon_amount, original_total_amount, feight_fee, pay_type, order_status, receiver_name, receiver_phone, receiver_tag_name, receiver_province, receiver_city, receiver_district, receiver_address, payment_time, delivery_time, receive_time, remark, cancel_time, cancel_reason from order_info
and user_id = #{userId}
and order_status = #{orderStatus}
and is_deleted = 0
order by id desc
